dark.style:
QWidget {
    background-color: #121212;
    color: white;
    font-family: 'Segoe UI', sans-serif;
}
QPushButton {
    background-color: #1E1E1E;
    border: 1px solid #2E2E2E;
    border-radius: 10px;
    padding: 10px 20px;
    color: #ffffff;
    font-size: 16px;
}
QPushButton:hover {
    background-color: #232323;
    border: 1px solid #00aaff;
}
QPushButton:pressed {
    background-color: #0077cc;
    color: white;
}

QTabWidget::pane {
    border: none;
    background: transparent;
    margin: 0px;
    padding: 0px;
}
QTabBar {
    border: none;
    background: transparent;
}
QTabBar::tab {
    background: #363636;
    color: #cccccc;
    padding: 8px 24px 8px 16px;
    margin-right: 4px;
    border: 1px solid transparent;
    font-weight: 500;
    min-width: 120px;
    border-radius: 6px;
    margin-top: 4px;
    margin-bottom: 2px;
}
QTabBar::tab:selected {
    background: #363636;
    color: #ffffff;
    border: 1px solid #007acc;
    margin-top: 0px;
    margin-bottom: 0px;
    margin-right: 6px;
}
QTabBar::tab:!selected {
    background: #363636;
    margin-right: 6px;
}
QTabBar::tab:hover {
    background: #3e3e42;
    margin-right: 6px;
}
QTabBar::tab:selected:hover {
    background: #363636;
    border: 1px solid #007acc;
    margin-right: 6px;
}
QTabBar::close-button {
    margin-right: 8px;
}
QTabBar::tab:!selected QTabBar::close-button {
    width: 0px;
    height: 0px;
    margin: 0px;
    padding: 0px;
}

LineBar {
    background: #1e1e1e;
    border: none;
    border-right: 1px solid #2d2d2d;
}

QPlainTextEdit {
    background-color: #1e1e1e;
    color: #d4d4d4;
    border: 1px solid #3e3e3e;
    font-family: 'Consolas', monospace;
    font-size: 12px;
}

IconBarButton {
    background: transparent;
    border: none;
    border-radius: 4px;
}
IconBarButton:hover {
    background: #2a2d2e;
}
IconBarButton:checked {
    background: #37373d;
    border-left: 2px solid #007acc;
}

ActivityBar {
    background-color: #333333;
    border: none;
    border-right: 1px solid #2d2d2d;
}
